import React, { useState } from 'react';
import logo from '../img/logo.png';
import produce from 'immer';
import axios from 'axios';

const foundingDates = [
  {
    id: 'founding-date-new',
    name: 'foundingDate',
    label: '신생',
  },
  {
    id: 'founding-date-5years',
    name: 'foundingDate',
    label: '5년 이상',
  },
  {
    id: 'founding-date-10years',
    name: 'foundingDate',
    label: '10년 이상',
  },
  {
    id: 'founding-date-20years',
    name: 'foundingDate',
    label: '20년 이상',
  },
];

const revenues = [
  {
    id: 'revenue-none',
    name: 'revenue',
    label: '무관',
    checked: false,
  },
  {
    id: 'revenue-0',
    name: 'revenue',
    label: '0 - 10억 사이',
    checked: false,
  },
  {
    id: 'revenue-1b',
    name: 'revenue',
    label: '10억 이상',
    checked: false,
  },
  {
    id: 'revenue-5b',
    name: 'revenue',
    label: '50억 이상',
    checked: false,
  },
  {
    id: 'revenue-10b',
    name: 'revenue',
    label: '100억 이상',
    checked: false,
  },
  {
    id: 'revenue-50b',
    name: 'revenue',
    label: '500억 이상',
    checked: false,
  },
];

const Filter = () => {
  const [form, setForm] = useState({
    foundingDate: '',
    revenue: '',
  });

  const onChange = event => {
    console.log(event.target.value);
    setForm(
      produce(form, draft => {
        draft[event.target.name] = event.target.value;
      })
    );
  };

  return (
    <div className="filter">
      <h1>
        <img src={logo} alt="" width="200" />
      </h1>
      <h2>Filter</h2>
      <SelectFilter></SelectFilter>
    </div>
  );
};

export default Filter;
