import './Dashboard.scss';
import React, { useState, useMemo, useEffect, useContext } from 'react';
import { useTable, usePagination } from 'react-table';
import axios from 'axios';
import JobPostContext from '../context/job-post-context';

const DashboardContext = () => {
  const { state } = useContext(JobPostContext);
  // console.log(state);

  const Columns = [
    {
      Header: '제목',
      accessor: 'title',
    },
    {
      Header: '회사이름',
      accessor: 'companyName',
    },
    {
      Header: '설립일',
      accessor: 'foundingDate',
    },
    {
      Header: '매출액',
      accessor: 'revenue',
    },
    {
      Header: '회사사원수',
      accessor: 'employee',
    },
  ];

  const columns = useMemo(() => Columns, []);

  const tableInstance = useTable(
    {
      columns: columns,
      data: state.posts,
    },
    usePagination
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    prepareRow,
    canPreviousPage,
    canNextPage,
  } = tableInstance;

  return (
    <div className="dashboard">
      {state.posts.length ? (
        <>
          <table {...getTableProps()}>
            <thead>
              {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map(column => (
                    <th {...column.getHeaderProps()}>
                      {column.render('Header')}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {page.map(row => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map((cell, index) => {
                      // console.log(state.posts);
                      // console.log(index);
                      // console.log(state.posts.map((post, index) => post));
                      // console.log(state.posts[0].url);
                      return (
                        <td {...cell.getCellProps()}>
                          {console.log(state.posts.map(post => post))}
                          {index ? (
                            cell.render('Cell')
                          ) : (
                            <a
                              href={state.posts.map(post => post.url)}
                              target="_blank"
                              rel="noreferrer"
                            >
                              {cell.render('Cell')}
                            </a>
                          )}
                        </td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div>
            <button onClick={() => previousPage()} disabled={!canPreviousPage}>
              Previous
            </button>
            <button onClick={() => nextPage()} disabled={!canNextPage}>
              Next
            </button>
          </div>
        </>
      ) : (
        '로딩중'
      )}
    </div>
  );
};

export default DashboardContext;
