import './Filter.scss';
import React, { useState } from 'react';
import logo from '../img/logo.png';
import produce from 'immer';
import axios from 'axios';

const foundingDates = [
  {
    id: 'founding-date-none',
    name: 'foundingDate',
    label: '무관',
  },
  {
    id: 'founding-date-new',
    name: 'foundingDate',
    label: '신생',
  },
  {
    id: 'founding-date-5years',
    name: 'foundingDate',
    label: '5년 이상',
  },
  {
    id: 'founding-date-10years',
    name: 'foundingDate',
    label: '10년 이상',
  },
  {
    id: 'founding-date-20years',
    name: 'foundingDate',
    label: '20년 이상',
  },
];

const revenues = [
  {
    id: 'revenue-none',
    name: 'revenue',
    label: '무관',
    checked: false,
  },
  {
    id: 'revenue-0',
    name: 'revenue',
    label: '0 - 10억 사이',
    checked: false,
  },
  {
    id: 'revenue-1b',
    name: 'revenue',
    label: '10억 이상',
    checked: false,
  },
  {
    id: 'revenue-5b',
    name: 'revenue',
    label: '50억 이상',
    checked: false,
  },
  {
    id: 'revenue-10b',
    name: 'revenue',
    label: '100억 이상',
    checked: false,
  },
  {
    id: 'revenue-50b',
    name: 'revenue',
    label: '500억 이상',
    checked: false,
  },
];

const employee = [
  {
    id: 'employee-none',
    name: 'employee',
    label: '무관',
    checked: false,
  },
  {
    id: 'employee-0',
    name: 'employee',
    label: '0 - 10억 사이',
    checked: false,
  },
  {
    id: 'employee-1b',
    name: 'employee',
    label: '10억 이상',
    checked: false,
  },
  {
    id: 'employee-5b',
    name: 'employee',
    label: '50억 이상',
    checked: false,
  },
  {
    id: 'employee-10b',
    name: 'employee',
    label: '100억 이상',
    checked: false,
  },
  {
    id: 'employee-50b',
    name: 'employee',
    label: '500억 이상',
    checked: false,
  },
];

const Filter = () => {
  const [form, setForm] = useState({
    foundingDate: '',
    revenue: '',
  });

  const onChange = event => {
    console.log(event.target.value);
    setForm(
      produce(form, draft => {
        draft[event.target.name] = event.target.value;
      })
    );
  };

  // const filterUrl = '주소'
  // const onSubmit = () => {
  //   axios.post(filterUrl, form).then(res=> {
  //     console.log(res.data)
  //   })
  // };

  return (
    <div className="filter">
      <h1>
        <img src={logo} alt="" width="200" />
      </h1>
      <h2>Filter</h2>
      <div>
        <h3>설립일</h3>
        <div onChange={onChange}>
          {foundingDates.map(foundingDate => (
            <>
              <input
                type="radio"
                id={foundingDate.id}
                value={foundingDate.id}
                name={foundingDate.name}
              />
              <label htmlFor={foundingDate.id}>{foundingDate.label}</label>
            </>
          ))}
        </div>
      </div>

      <div>
        <h3>매출액</h3>
        <div onChange={onChange}>
          {revenues.map(revenue => (
            <>
              <input
                type="radio"
                id={revenue.id}
                value={revenue.id}
                name={revenue.name}
              />
              <label htmlFor={revenue.id}>{revenue.label}</label>
            </>
          ))}
        </div>
      </div>

      {/* <button onClick={onSubmit}>필터 적용하기</button> */}
    </div>
  );
};

export default Filter;
